import React, { useState, useEffect } from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';


const ListContainer = styled.div`
    // display: flex;
    // flex-wrap: wrap;
    // display: grid;
    // grid-template-columns: repeat(5, 1fr);
    // grid-template-rows: repeat(5, 1fr);
    // grid-column-gap: 5px;
    // grid-row-gap: 5px;
    display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: center;
  align-items: auto;
  align-content: start
  :after {
      display: block;
  content: " placeholder ";
  margin: 5px;
  flex: 999 999 auto;
}
`;

const MovieBox = styled(Link)`
    flex-basis: 20%;
    background: #000;
    position: relative;
    overflow: hidden;
    color: var(--fontcolor);
    overflow: hidden;
    // visibility: hidden;

    @media (max-width: 768px) {
      flex-basis: 25%;
    }
`;

interface IPopularMovie {
    map(arg0: (popularMovie: any) => JSX.Element): React.ReactNode;
    id: number;
    backdrop_path: string;
    original_title: string;
  }

const Home = () => {
    useEffect(() => {
        getPopulateMovies();
    }, []); 

    const [ popularMovies, setPopularMovies ] = useState([] as any[]);
    const getPopulateMovies = async () => {
        const response = await fetch('https://api.themoviedb.org/3/movie/popular?api_key=5bcd76c471e358e143b8721211e000d4')
        const allPopularMovies: any = await response.json();

        console.log('allPopularMovies :', allPopularMovies);
        setPopularMovies(allPopularMovies.results);
    }

    return (
        <div>
            <div>search bar</div>
           {popularMovies.length ? <h1>Films populaires</h1> : ''}
            <ListContainer>
                 {!popularMovies ? '' : popularMovies.map((popularMovie) => {
                    console.log(popularMovie);

                    return (
                        <MovieBox key={popularMovie} to={`/item-page/${popularMovie.id}`}>
                            <img src={`https://image.tmdb.org/t/p/w500/${popularMovie.backdrop_path}`}></img>
                            {/* {popularMovie.title} */}
                        </MovieBox>
                    )})}

            </ListContainer>
        </div>
    )
}

export default Home
